mapper.select:
select count(*) as tradeCount, date_format(create_time, #{query.timeFormat}) as tradeTime from t_contract where delete_flag = 0
and create_time between #{query.timeRange[0]} and #{query.timeRange[1]}
and status = #{query.status}
group by date_format(create_time, #{query.timeFormat}) order by tradeTime

SELECT
DATE_FORMAT(create_time, '%H') AS tradeTime,  
COUNT(*) AS tradeCount  
FROM t_contract
WHERE delete_flag = 0
AND create_time BETWEEN #{query.timeRange[0]} AND #{query.timeRange[1]}

    and status = #{query.status}

GROUP BY DATE_FORMAT(create_time, '%H')
ORDER BY tradeTime;

select any_value(count(*)) as tradeCount,
CAST( floor((datediff(create_time, date_format(create_time, '%Y-%01-01')) + weekday(date_format(create_time, '%Y-%01-01')) + 1)/7) + 1 AS CHAR) as tradeTime from t_contract where delete_flag = 0
and create_time between STR_TO_DATE(#{query.timeRange[0]}, '%Y-%m-%d %H:%i:%s') and STR_TO_DATE(#{query.timeRange[1]}, '%Y-%m-%d %H:%i:%s')
and status = #{query.status}
group by tradeTime order by tradeTime;
